import React, { useState, useEffect } from 'react';
import { Container, Row, Col, Card, ListGroup } from 'react-bootstrap';
import JobForm from '../components/JobForm';
import MatchResults from '../components/MatchResults';
import { jobAPI } from '../services/api';

const RecruiterDashboard = () => {
  const [jobs, setJobs] = useState([]);
  const [selectedJob, setSelectedJob] = useState(null);

  const loadJobs = async () => {
    try {
      const response = await jobAPI.getAll();
      setJobs(response.data);
    } catch (error) {
      console.error('Error loading jobs:', error);
    }
  };

  useEffect(() => {
    loadJobs();
  }, []);

  return (
    <Container fluid className="mt-4">
      <Row>
        <Col md={4}>
          <JobForm onJobCreated={loadJobs} />
          <Card className="mt-3">
            <Card.Header><h5>Posted Jobs</h5></Card.Header>
            <Card.Body style={{ maxHeight: '400px', overflowY: 'auto' }}>
              <ListGroup variant="flush">
                {jobs.map((job) => (
                  <ListGroup.Item 
                    key={job._id} 
                    action 
                    active={selectedJob?._id === job._id}
                    onClick={() => setSelectedJob(job)}
                  >
                    <strong>{job.title}</strong><br />
                    <small>{job.company} - {job.location}</small>
                  </ListGroup.Item>
                ))}
              </ListGroup>
            </Card.Body>
          </Card>
        </Col>
        <Col md={8}>
          {selectedJob ? (
            <MatchResults jobId={selectedJob._id} jobTitle={selectedJob.title} />
          ) : (
            <Card>
              <Card.Body className="text-center">
                <h5>Select a job to view matches</h5>
                <p>Click on a job from the list to see AI-powered candidate matches.</p>
              </Card.Body>
            </Card>
          )}
        </Col>
      </Row>
    </Container>
  );
};

export default RecruiterDashboard;
