import React, { useState, useEffect } from 'react';
import { Container, Row, Col, Card, ListGroup, Badge } from 'react-bootstrap';
import ResumeUpload from '../components/ResumeUpload';
import { resumeAPI, jobAPI } from '../services/api';

const CandidateDashboard = () => {
  const [resumes, setResumes] = useState([]);
  const [jobs, setJobs] = useState([]);

  const loadData = async () => {
    try {
      const [resumeResponse, jobResponse] = await Promise.all([
        resumeAPI.getAll(),
        jobAPI.getAll()
      ]);
      setResumes(resumeResponse.data);
      setJobs(jobResponse.data);
    } catch (error) {
      console.error('Error loading data:', error);
    }
  };

  useEffect(() => {
    loadData();
  }, []);

  return (
    <div className="bg-light min-vh-100">
      <Container fluid className="py-4">
        <Row className="mb-4">
          <Col>
            <h2 className="text-success">üë®üíº Candidate Dashboard</h2>
            <p className="text-muted">Upload your resume and discover matching job opportunities</p>
          </Col>
        </Row>

        <Row className="g-4">
          <Col lg={6}>
            <ResumeUpload onResumeUploaded={loadData} />

            <Card className="card-hover mt-4">
              <Card.Header className="bg-success text-white d-flex justify-content-between align-items-center">
                <h5 className="mb-0">üìÑ Your Resumes</h5>
                <Badge bg="light" text="dark">{resumes.length}</Badge>
              </Card.Header>
              <Card.Body className="p-0" style={{ maxHeight: '400px', overflowY: 'auto' }}>
                {resumes.length === 0 ? (
                  <div className="text-center py-4">
                    <div className="display-1 mb-3">üìÑ</div>
                    <p className="text-muted">No resumes uploaded yet</p>
                  </div>
                ) : (
                  <ListGroup variant="flush">
                    {resumes.map((resume) => (
                      <ListGroup.Item key={resume._id}>
                        <div className="d-flex justify-content-between align-items-start">
                          <div>
                            <strong className="text-success">{resume.candidateName}</strong>
                            <br />
                            <small className="text-muted">
                              üìß {resume.email} ‚Ä¢ üìû {resume.phone}
                            </small>
                            <br />
                            <small className="text-primary">üìÅ {resume.fileName}</small>
                            <br />
                            <div className="mt-2">
                              {resume.skills.slice(0, 3).map((skill, idx) => (
                                <Badge key={idx} bg="outline-success" className="me-1 mb-1" style={{border: '1px solid #28a745', color: '#28a745'}}>
                                  {skill}
                                </Badge>
                              ))}
                              {resume.skills.length > 3 && (
                                <Badge bg="secondary">+{resume.skills.length - 3} more</Badge>
                              )}
                            </div>
                          </div>
                          <Badge bg="success">
                            {new Date(resume.uploadedAt).toLocaleDateString()}
                          </Badge>
                        </div>
                      </ListGroup.Item>
                    ))}
                  </ListGroup>
                )}
              </Card.Body>
            </Card>
          </Col>

          <Col lg={6}>
            <Card className="card-hover">
              <Card.Header className="bg-primary text-white d-flex justify-content-between align-items-center">
                <h5 className="mb-0">üîç Available Jobs</h5>
                <Badge bg="light" text="dark">{jobs.length}</Badge>
              </Card.Header>
              <Card.Body className="p-0" style={{ maxHeight: '600px', overflowY: 'auto' }}>
                {jobs.length === 0 ? (
                  <div className="text-center py-4">
                    <div className="display-1 mb-3">üíº</div>
                    <p className="text-muted">No jobs available</p>
                  </div>
                ) : (
                  <ListGroup variant="flush">
                    {jobs.map((job) => (
                      <ListGroup.Item key={job._id} className="job-item">
                        <div className="d-flex justify-content-between align-items-start">
                          <div className="flex-grow-1">
                            <div className="d-flex align-items-center mb-2">
                              <strong className="text-primary me-2">{job.title}</strong>
                              <Badge bg="primary">{job.company}</Badge>
                            </div>
                            <p className="text-muted mb-2" style={{fontSize: '0.9rem'}}>
                              üìç {job.location} ‚Ä¢ üí∞ {job.salary}
                            </p>
                            <p className="mb-2" style={{fontSize: '0.85rem', lineHeight: '1.4'}}>
                              {job.description.length > 120 ? 
                                `${job.description.substring(0, 120)}...` : 
                                job.description
                              }
                            </p>
                            <div className="mb-2">
                              <small className="text-muted fw-bold">Key Requirements:</small>
                              <br />
                              <small className="text-dark">
                                {job.requirements.length > 100 ? 
                                  `${job.requirements.substring(0, 100)}...` : 
                                  job.requirements
                                }
                              </small>
                            </div>
                            <small className="text-muted">
                              Posted by {job.postedBy} ‚Ä¢ {new Date(job.createdAt).toLocaleDateString()}
                            </small>
                          </div>
                        </div>
                      </ListGroup.Item>
                    ))}
                  </ListGroup>
                )}
              </Card.Body>
            </Card>
          </Col>
        </Row>
      </Container>
    </div>
  );
};

export default CandidateDashboard;
