import React, { useState, useEffect } from 'react';
import { Container, Row, Col, Card, ListGroup } from 'react-bootstrap';
import ResumeUpload from '../components/ResumeUpload';
import { resumeAPI, jobAPI } from '../services/api';

const CandidateDashboard = () => {
  const [resumes, setResumes] = useState([]);
  const [jobs, setJobs] = useState([]);

  const loadData = async () => {
    try {
      const [resumeResponse, jobResponse] = await Promise.all([
        resumeAPI.getAll(),
        jobAPI.getAll()
      ]);
      setResumes(resumeResponse.data);
      setJobs(jobResponse.data);
    } catch (error) {
      console.error('Error loading data:', error);
    }
  };

  useEffect(() => {
    loadData();
  }, []);

  return (
    <Container fluid className="mt-4">
      <Row>
        <Col md={6}>
          <ResumeUpload onResumeUploaded={loadData} />
          <Card className="mt-3">
            <Card.Header><h5>Your Resumes</h5></Card.Header>
            <Card.Body style={{ maxHeight: '300px', overflowY: 'auto' }}>
              <ListGroup variant="flush">
                {resumes.map((resume) => (
                  <ListGroup.Item key={resume._id}>
                    <strong>{resume.candidateName}</strong><br />
                    <small>{resume.fileName} - {new Date(resume.uploadedAt).toLocaleDateString()}</small>
                  </ListGroup.Item>
                ))}
              </ListGroup>
            </Card.Body>
          </Card>
        </Col>
        <Col md={6}>
          <Card>
            <Card.Header><h5>Available Jobs</h5></Card.Header>
            <Card.Body style={{ maxHeight: '500px', overflowY: 'auto' }}>
              <ListGroup variant="flush">
                {jobs.map((job) => (
                  <ListGroup.Item key={job._id}>
                    <strong>{job.title}</strong> - {job.company}<br />
                    <small>{job.location}</small><br />
                    <small className="text-muted">{job.description.substring(0, 100)}...</small>
                  </ListGroup.Item>
                ))}
              </ListGroup>
            </Card.Body>
          </Card>
        </Col>
      </Row>
    </Container>
  );
};

export default CandidateDashboard;
