import React, { useState, useEffect } from 'react';
import ResumeUpload from '../components/ResumeUpload';
import { resumeAPI, jobAPI } from '../services/api';

const CandidateDashboard = () => {
  const [resumes, setResumes] = useState([]);
  const [jobs, setJobs] = useState([]);

  const loadData = async () => {
    try {
      const [resumeResponse, jobResponse] = await Promise.all([
        resumeAPI.getAll(),
        jobAPI.getAll()
      ]);
      setResumes(resumeResponse.data);
      setJobs(jobResponse.data);
    } catch (error) {
      console.error('Error loading data:', error);
    }
  };

  useEffect(() => {
    loadData();
  }, []);

  return (
    <div style={{ backgroundColor: 'var(--bg-secondary)', minHeight: '100vh', paddingTop: 'var(--spacing-lg)' }}>
      <div className="main-container">
        <div className="mb-xl">
          <h1 className="page-title">üë®üíº Candidate Dashboard</h1>
          <p className="page-subtitle">Upload your resume and discover matching job opportunities</p>
        </div>

        <div className="grid-2 gap-lg">
          <div>
            <ResumeUpload onResumeUploaded={loadData} />

            <div className="card-custom" style={{ marginTop: 'var(--spacing-lg)' }}>
              <div className="card-header-custom flex items-center justify-between">
                <h3 className="section-title" style={{ margin: 0 }}>üìÑ Your Resumes</h3>
                <span className="badge-custom badge-primary">{resumes.length}</span>
              </div>
              <div style={{ maxHeight: '400px', overflowY: 'auto' }}>
                {resumes.length === 0 ? (
                  <div className="empty-state">
                    <div className="empty-icon">üìÑ</div>
                    <h4>No resumes uploaded yet</h4>
                    <p>Upload your first resume to get started</p>
                  </div>
                ) : (
                  resumes.map((resume) => (
                    <div key={resume._id} className="list-item">
                      <div className="flex justify-between items-start">
                        <div>
                          <div style={{ fontWeight: '600', color: 'var(--success-color)', marginBottom: '4px' }}>
                            {resume.candidateName}
                          </div>
                          <div style={{ fontSize: '0.875rem', color: 'var(--text-secondary)', marginBottom: '4px' }}>
                            üìß {resume.email} ‚Ä¢ üìû {resume.phone}
                          </div>
                          <div style={{ fontSize: '0.875rem', color: 'var(--primary-color)', marginBottom: '8px' }}>
                            üìÅ {resume.fileName}
                          </div>
                          <div style={{ display: 'flex', flexWrap: 'wrap', gap: '4px' }}>
                            {resume.skills.slice(0, 3).map((skill, idx) => (
                              <span key={idx} className="badge-custom" style={{
                                backgroundColor: 'transparent',
                                border: '1px solid var(--success-color)',
                                color: 'var(--success-color)',
                                fontSize: '0.75rem'
                              }}>
                                {skill}
                              </span>
                            ))}
                            {resume.skills.length > 3 && (
                              <span className="badge-custom badge-secondary" style={{ fontSize: '0.75rem' }}>
                                +{resume.skills.length - 3}
                              </span>
                            )}
                          </div>
                        </div>
                        <span className="badge-custom badge-primary">
                          {new Date(resume.uploadedAt).toLocaleDateString()}
                        </span>
                      </div>
                    </div>
                  ))
                )}
              </div>
            </div>
          </div>

          <div>
            <div className="card-custom">
              <div className="card-header-custom flex items-center justify-between">
                <h3 className="section-title" style={{ margin: 0 }}>üîç Available Jobs</h3>
                <span className="badge-custom badge-primary">{jobs.length}</span>
              </div>
              <div style={{ maxHeight: '600px', overflowY: 'auto' }}>
                {jobs.length === 0 ? (
                  <div className="empty-state">
                    <div className="empty-icon">üíº</div>
                    <h4>No jobs available</h4>
                    <p>Check back later for new opportunities</p>
                  </div>
                ) : (
                  jobs.map((job) => (
                    <div key={job._id} className="list-item">
                      <div>
                        <div className="flex items-center gap-sm mb-xs">
                          <div style={{ fontWeight: '600', color: 'var(--primary-color)' }}>
                            {job.title}
                          </div>
                          <span className="badge-custom badge-primary">{job.company}</span>
                        </div>
                        <div style={{ fontSize: '0.875rem', color: 'var(--text-secondary)', marginBottom: 'var(--spacing-xs)' }}>
                          üìç {job.location} ‚Ä¢ üí∞ {job.salary}
                        </div>
                        <p style={{ 
                          fontSize: '0.875rem', 
                          lineHeight: '1.4', 
                          marginBottom: 'var(--spacing-xs)',
                          color: 'var(--text-primary)'
                        }}>
                          {job.description.length > 120 ? 
                            `${job.description.substring(0, 120)}...` : 
                            job.description
                          }
                        </p>
                        <div style={{ marginBottom: 'var(--spacing-xs)' }}>
                          <div style={{ fontSize: '0.75rem', fontWeight: '600', color: 'var(--text-secondary)', marginBottom: '4px' }}>
                            Key Requirements:
                          </div>
                          <div style={{ fontSize: '0.875rem', color: 'var(--text-primary)' }}>
                            {job.requirements.length > 100 ? 
                              `${job.requirements.substring(0, 100)}...` : 
                              job.requirements
                            }
                          </div>
                        </div>
                        <div style={{ fontSize: '0.75rem', color: 'var(--text-muted)' }}>
                          Posted by {job.postedBy} ‚Ä¢ {new Date(job.createdAt).toLocaleDateString()}
                        </div>
                      </div>
                    </div>
                  ))
                )}
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default CandidateDashboard;
