import React, { useState, useEffect } from 'react';
import JobForm from '../components/JobForm';
import MatchResults from '../components/MatchResults';
import { jobAPI } from '../services/api';

const RecruiterDashboard = () => {
  const [jobs, setJobs] = useState([]);
  const [selectedJob, setSelectedJob] = useState(null);

  const loadJobs = async () => {
    try {
      const response = await jobAPI.getAll();
      setJobs(response.data);
    } catch (error) {
      console.error('Error loading jobs:', error);
    }
  };

  useEffect(() => {
    loadJobs();
  }, []);

  return (
    <div style={{ backgroundColor: 'var(--bg-secondary)', minHeight: '100vh', paddingTop: 'var(--spacing-lg)' }}>
      <div className="main-container">
        <div className="mb-xl">
          <h1 className="page-title">üëî Recruiter Dashboard</h1>
          <p className="page-subtitle">Post jobs and find the perfect candidates with AI-powered matching</p>
        </div>

        <div className="grid-dashboard">
          <div>
            <JobForm onJobCreated={loadJobs} />

            <div className="card-custom" style={{ marginTop: 'var(--spacing-lg)' }}>
              <div className="card-header-custom flex items-center justify-between">
                <h3 className="section-title" style={{ margin: 0 }}>üíº Posted Jobs</h3>
                <span className="badge-custom badge-primary">{jobs.length}</span>
              </div>
              <div style={{ maxHeight: '500px', overflowY: 'auto' }}>
                {jobs.length === 0 ? (
                  <div className="empty-state">
                    <div className="empty-icon">üíº</div>
                    <h4>No jobs posted yet</h4>
                    <p>Create your first job posting to get started</p>
                  </div>
                ) : (
                  jobs.map((job) => (
                    <div
                      key={job._id}
                      className={`list-item ${selectedJob?._id === job._id ? 'active' : ''}`}
                      onClick={() => setSelectedJob(job)}
                    >
                      <div className="flex justify-between items-center">
                        <div>
                          <div style={{ fontWeight: '600', color: 'var(--primary-color)', marginBottom: '4px' }}>
                            {job.title}
                          </div>
                          <div style={{ fontSize: '0.875rem', color: 'var(--text-secondary)', marginBottom: '4px' }}>
                            üè¢ {job.company} ‚Ä¢ üìç {job.location}
                          </div>
                          <div style={{ fontSize: '0.875rem', color: 'var(--success-color)' }}>
                            {job.salary}
                          </div>
                        </div>
                        <span className="badge-custom badge-secondary">
                          {new Date(job.createdAt).toLocaleDateString()}
                        </span>
                      </div>
                    </div>
                  ))
                )}
              </div>
            </div>
          </div>

          <div>
            {selectedJob ? (
              <>
                <div className="card-custom mb-lg">
                  <div className="card-body-custom">
                    <div className="flex justify-between items-start mb-md">
                      <div>
                        <h2 style={{ color: 'var(--primary-color)', marginBottom: 'var(--spacing-xs)' }}>
                          {selectedJob.title}
                        </h2>
                        <p style={{ color: 'var(--text-secondary)', marginBottom: 'var(--spacing-sm)' }}>
                          üè¢ {selectedJob.company} ‚Ä¢ üìç {selectedJob.location} ‚Ä¢ üí∞ {selectedJob.salary}
                        </p>
                      </div>
                      <div style={{ textAlign: 'right' }}>
                        <div className="badge-custom badge-secondary mb-xs">
                          Posted by {selectedJob.postedBy}
                        </div>
                        <div style={{ fontSize: '0.875rem', color: 'var(--text-muted)' }}>
                          {new Date(selectedJob.createdAt).toLocaleDateString()}
                        </div>
                      </div>
                    </div>
                    <div className="mb-sm">
                      <h4 style={{ fontSize: '1rem', fontWeight: '600', marginBottom: 'var(--spacing-xs)' }}>Description:</h4>
                      <p style={{ color: 'var(--text-secondary)' }}>{selectedJob.description}</p>
                    </div>
                    <div>
                      <h4 style={{ fontSize: '1rem', fontWeight: '600', marginBottom: 'var(--spacing-xs)' }}>Requirements:</h4>
                      <p style={{ color: 'var(--text-secondary)' }}>{selectedJob.requirements}</p>
                    </div>
                  </div>
                </div>
                <MatchResults jobId={selectedJob._id} jobTitle={selectedJob.title} />
              </>
            ) : (
              <div className="card-custom">
                <div className="card-body-custom text-center" style={{ padding: 'var(--spacing-xl)' }}>
                  <div className="empty-icon">üéØ</div>
                  <h3 style={{ marginBottom: 'var(--spacing-sm)' }}>Select a job to view matches</h3>
                  <p style={{ color: 'var(--text-secondary)' }}>
                    Click on a job from the list to see AI-powered candidate matches and detailed analytics.
                  </p>
                </div>
              </div>
            )}
          </div>
        </div>
      </div>
    </div>
  );
};

export default RecruiterDashboard;
