import React, { useState } from 'react';
import { jobAPI } from '../services/api';

const JobForm = ({ onJobCreated, onClose }) => {
  const [job, setJob] = useState({
    title: '', company: '', description: '', requirements: '', location: '', salary: '', postedBy: ''
  });
  const [message, setMessage] = useState('');
  const [loading, setLoading] = useState(false);

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    try {
      await jobAPI.create(job);
      setJob({ title: '', company: '', description: '', requirements: '', location: '', salary: '', postedBy: '' });
      onJobCreated?.();
      onClose?.();
    } catch (error) {
      setMessage('Error posting job ‚ùå');
    }
    setLoading(false);
  };

  return (
    <>
      {/* Modal Backdrop */}
      <div 
        style={{
          position: 'fixed',
          top: 0,
          left: 0,
          right: 0,
          bottom: 0,
          backgroundColor: 'rgba(0, 0, 0, 0.5)',
          zIndex: 1000,
          display: 'flex',
          alignItems: 'center',
          justifyContent: 'center',
          padding: '20px'
        }}
        onClick={onClose}
      >
        {/* Modal Content */}
        <div 
          className="card-custom"
          style={{
            width: '100%',
            maxWidth: '600px',
            maxHeight: '90vh',
            overflowY: 'auto'
          }}
          onClick={(e) => e.stopPropagation()}
        >
          <div className="card-header-custom flex items-center justify-between">
            <h2 className="section-title" style={{ margin: 0, border: 'none', padding: 0 }}>üíº Post New Job</h2>
            <button 
              onClick={onClose}
              style={{
                background: 'none',
                border: 'none',
                fontSize: '24px',
                cursor: 'pointer',
                color: '#6b7280',
                padding: '4px'
              }}
            >
              √ó
            </button>
          </div>
          <div className="card-body-custom">
            {message && (
              <div style={{
                padding: '12px 16px',
                borderRadius: 'var(--radius)',
                marginBottom: '20px',
                backgroundColor: '#fef2f2',
                color: 'var(--danger-color)',
                border: '1px solid #fecaca'
              }}>
                {message}
              </div>
            )}

            <form onSubmit={handleSubmit}>
              <div className="grid-2">
                <div className="form-group-custom">
                  <label className="form-label">Job Title</label>
                  <input
                    className="form-control-custom"
                    value={job.title}
                    onChange={(e) => setJob({...job, title: e.target.value})}
                    placeholder="e.g. Full Stack Developer"
                    required
                  />
                </div>
                <div className="form-group-custom">
                  <label className="form-label">Company</label>
                  <input
                    className="form-control-custom"
                    value={job.company}
                    onChange={(e) => setJob({...job, company: e.target.value})}
                    placeholder="Your Company Name"
                    required
                  />
                </div>
              </div>

              <div className="form-group-custom">
                <label className="form-label">Job Description</label>
                <textarea
                  className="form-control-custom"
                  style={{ height: '92px', resize: 'vertical', paddingTop: '12px', paddingBottom: '12px' }}
                  value={job.description}
                  onChange={(e) => setJob({...job, description: e.target.value})}
                  placeholder="Describe the role and responsibilities..."
                  required
                />
              </div>

              <div className="form-group-custom">
                <label className="form-label">Requirements & Skills</label>
                <textarea
                  className="form-control-custom"
                  style={{ height: '92px', resize: 'vertical', paddingTop: '12px', paddingBottom: '12px' }}
                  value={job.requirements}
                  onChange={(e) => setJob({...job, requirements: e.target.value})}
                  placeholder="List required skills, experience, qualifications..."
                  required
                />
              </div>

              <div className="grid-3">
                <div className="form-group-custom">
                  <label className="form-label">Location</label>
                  <input
                    className="form-control-custom"
                    value={job.location}
                    onChange={(e) => setJob({...job, location: e.target.value})}
                    required
                  />
                </div>
                <div className="form-group-custom">
                  <label className="form-label">Salary Range</label>
                  <input
                    className="form-control-custom"
                    value={job.salary}
                    onChange={(e) => setJob({...job, salary: e.target.value})}
                  />
                </div>
                <div className="form-group-custom">
                  <label className="form-label">Posted By</label>
                  <input
                    className="form-control-custom"
                    value={job.postedBy}
                    onChange={(e) => setJob({...job, postedBy: e.target.value})}
                    required
                  />
                </div>
              </div>

              <div className="flex gap-sm">
                <button 
                  type="button" 
                  onClick={onClose}
                  style={{
                    background: '#f3f4f6',
                    border: '1px solid #d1d5db',
                    color: '#374151',
                    padding: '12px 24px',
                    borderRadius: 'var(--radius)',
                    fontWeight: '500',
                    fontSize: '14px',
                    height: '48px',
                    cursor: 'pointer',
                    flex: 1
                  }}
                >
                  Cancel
                </button>
                <button type="submit" className="btn-primary-custom" style={{ flex: 2 }} disabled={loading}>
                  {loading ? 'üîÑ Posting...' : 'üöÄ Post Job'}
                </button>
              </div>
            </form>
          </div>
        </div>
      </div>
    </>
  );
};

export default JobForm;
