import React, { useState, useEffect } from 'react';
import { Container, Row, Col, Card, ListGroup, Badge } from 'react-bootstrap';
import JobForm from '../components/JobForm';
import MatchResults from '../components/MatchResults';
import { jobAPI } from '../services/api';

const RecruiterDashboard = () => {
  const [jobs, setJobs] = useState([]);
  const [selectedJob, setSelectedJob] = useState(null);

  const loadJobs = async () => {
    try {
      const response = await jobAPI.getAll();
      setJobs(response.data);
    } catch (error) {
      console.error('Error loading jobs:', error);
    }
  };

  useEffect(() => {
    loadJobs();
  }, []);

  return (
    <div className="bg-light min-vh-100">
      <Container fluid className="py-4">
        <Row className="mb-4">
          <Col>
            <h2 className="text-primary">üëî Recruiter Dashboard</h2>
            <p className="text-muted">Post jobs and find the perfect candidates with AI-powered matching</p>
          </Col>
        </Row>

        <Row className="g-4">
          <Col lg={4}>
            <JobForm onJobCreated={loadJobs} />

            <Card className="card-hover mt-4">
              <Card.Header className="bg-secondary text-white d-flex justify-content-between align-items-center">
                <h5 className="mb-0">üíº Posted Jobs</h5>
                <Badge bg="light" text="dark">{jobs.length}</Badge>
              </Card.Header>
              <Card.Body className="p-0" style={{ maxHeight: '500px', overflowY: 'auto' }}>
                {jobs.length === 0 ? (
                  <div className="text-center py-4">
                    <div className="display-1 mb-3">üíº</div>
                    <p className="text-muted">No jobs posted yet</p>
                  </div>
                ) : (
                  <ListGroup variant="flush">
                    {jobs.map((job) => (
                      <ListGroup.Item 
                        key={job._id} 
                        action 
                        className={`job-item ${selectedJob?._id === job._id ? 'active' : ''}`}
                        onClick={() => setSelectedJob(job)}
                      >
                        <div className="d-flex justify-content-between align-items-start">
                          <div>
                            <strong className="text-primary">{job.title}</strong>
                            <br />
                            <small className="text-muted">
                              üè¢ {job.company} ‚Ä¢ üìç {job.location}
                            </small>
                            <br />
                            <small className="text-success">{job.salary}</small>
                          </div>
                          <Badge bg="primary" className="ms-2">
                            {new Date(job.createdAt).toLocaleDateString()}
                          </Badge>
                        </div>
                      </ListGroup.Item>
                    ))}
                  </ListGroup>
                )}
              </Card.Body>
            </Card>
          </Col>

          <Col lg={8}>
            {selectedJob ? (
              <>
                <Card className="card-hover mb-4">
                  <Card.Body>
                    <Row>
                      <Col md={8}>
                        <h4 className="text-primary">{selectedJob.title}</h4>
                        <p className="text-muted mb-2">
                          üè¢ {selectedJob.company} ‚Ä¢ üìç {selectedJob.location} ‚Ä¢ üí∞ {selectedJob.salary}
                        </p>
                        <p><strong>Description:</strong> {selectedJob.description}</p>
                        <p><strong>Requirements:</strong> {selectedJob.requirements}</p>
                      </Col>
                      <Col md={4} className="text-end">
                        <Badge bg="info" className="mb-2">Posted by {selectedJob.postedBy}</Badge>
                        <br />
                        <small className="text-muted">
                          Posted on {new Date(selectedJob.createdAt).toLocaleDateString()}
                        </small>
                      </Col>
                    </Row>
                  </Card.Body>
                </Card>
                <MatchResults jobId={selectedJob._id} jobTitle={selectedJob.title} />
              </>
            ) : (
              <Card className="card-hover text-center">
                <Card.Body className="py-5">
                  <div className="display-1 mb-4">üéØ</div>
                  <h4>Select a job to view matches</h4>
                  <p className="text-muted">Click on a job from the list to see AI-powered candidate matches and detailed analytics.</p>
                </Card.Body>
              </Card>
            )}
          </Col>
        </Row>
      </Container>
    </div>
  );
};

export default RecruiterDashboard;
