import React, { useState, useEffect } from 'react';
import { Table, Button, Card, Badge, Spinner, Row, Col, ProgressBar } from 'react-bootstrap';
import { jobAPI } from '../services/api';

const MatchResults = ({ jobId, jobTitle }) => {
  const [matches, setMatches] = useState([]);
  const [loading, setLoading] = useState(false);

  const loadMatches = async () => {
    try {
      const response = await jobAPI.getMatches(jobId);
      setMatches(response.data);
    } catch (error) {
      console.error('Error loading matches:', error);
    }
  };

  const runMatching = async () => {
    setLoading(true);
    try {
      await jobAPI.matchResumes(jobId);
      await loadMatches();
    } catch (error) {
      console.error('Error running matches:', error);
    }
    setLoading(false);
  };

  useEffect(() => {
    if (jobId) loadMatches();
  }, [jobId]);

  const getScoreColor = (score) => {
    if (score >= 80) return 'success';
    if (score >= 60) return 'warning';
    return 'danger';
  };

  const getScoreClass = (score) => {
    if (score >= 80) return 'match-score-excellent';
    if (score >= 60) return 'match-score-good';
    return 'match-score-poor';
  };

  const getScoreLabel = (score) => {
    if (score >= 80) return 'Excellent Match';
    if (score >= 60) return 'Good Match';
    return 'Needs Review';
  };

  return (
    <Card className="card-hover">
      <Card.Header className="bg-info text-white">
        <Row className="align-items-center">
          <Col>
            <h4 className="mb-0">üéØ Match Results: {jobTitle}</h4>
          </Col>
          <Col xs="auto">
            <Button 
              onClick={runMatching} 
              disabled={loading || !jobId}
              className="btn-gradient"
            >
              {loading ? (
                <>
                  <Spinner size="sm" className="me-2" />
                  ü§ñ AI Analyzing...
                </>
              ) : (
                'üöÄ Run AI Matching'
              )}
            </Button>
          </Col>
        </Row>
      </Card.Header>
      <Card.Body>
        {matches.length === 0 ? (
          <div className="text-center py-5">
            <div className="display-1 mb-3">üîç</div>
            <h5>No matches found yet</h5>
            <p className="text-muted">Click "Run AI Matching" to analyze candidate resumes against this job posting.</p>
          </div>
        ) : (
          <>
            <div className="mb-4">
              <Row className="g-3">
                <Col md={4}>
                  <Card className="stats-card text-center">
                    <Card.Body>
                      <h3 className="mb-0">{matches.length}</h3>
                      <small>Total Candidates</small>
                    </Card.Body>
                  </Card>
                </Col>
                <Col md={4}>
                  <Card className="bg-success text-white text-center">
                    <Card.Body>
                      <h3 className="mb-0">{matches.filter(m => m.score >= 80).length}</h3>
                      <small>Excellent Matches</small>
                    </Card.Body>
                  </Card>
                </Col>
                <Col md={4}>
                  <Card className="bg-warning text-white text-center">
                    <Card.Body>
                      <h3 className="mb-0">{matches.filter(m => m.score >= 60 && m.score < 80).length}</h3>
                      <small>Good Matches</small>
                    </Card.Body>
                  </Card>
                </Col>
              </Row>
            </div>

            <Table hover className="align-middle">
              <thead className="table-dark">
                <tr>
                  <th>Rank</th>
                  <th>Candidate</th>
                  <th>Contact</th>
                  <th>Match Score</th>
                  <th>Matching Skills</th>
                  <th>Uploaded</th>
                </tr>
              </thead>
              <tbody>
                {matches.map((match, index) => (
                  <tr key={match._id} className={index < 3 ? 'table-light' : ''}>
                    <td>
                      <Badge bg={index === 0 ? 'warning' : index === 1 ? 'secondary' : index === 2 ? 'dark' : 'light'} className="fs-6">
                        #{index + 1}
                      </Badge>
                    </td>
                    <td>
                      <div>
                        <strong>{match.resumeId.candidateName}</strong>
                        <br />
                        <small className="text-muted">{match.resumeId.education}</small>
                      </div>
                    </td>
                    <td>
                      <div>
                        <small>{match.resumeId.email}</small>
                        <br />
                        <small className="text-muted">{match.resumeId.phone}</small>
                      </div>
                    </td>
                    <td>
                      <div>
                        <Badge className={`${getScoreClass(match.score)} fs-5 mb-1`}>
                          {match.score}%
                        </Badge>
                        <br />
                        <small className="text-muted">{getScoreLabel(match.score)}</small>
                        <ProgressBar 
                          variant={getScoreColor(match.score)} 
                          now={match.score} 
                          className="mt-1" 
                          style={{height: '4px'}}
                        />
                      </div>
                    </td>
                    <td>
                      <div>
                        {match.matchingSkills.slice(0, 3).map((skill, idx) => (
                          <Badge key={idx} className="skill-badge me-1 mb-1">
                            {skill}
                          </Badge>
                        ))}
                        {match.matchingSkills.length > 3 && (
                          <Badge bg="secondary">+{match.matchingSkills.length - 3} more</Badge>
                        )}
                      </div>
                    </td>
                    <td>
                      <small className="text-muted">
                        {new Date(match.resumeId.uploadedAt).toLocaleDateString()}
                      </small>
                    </td>
                  </tr>
                ))}
              </tbody>
            </Table>
          </>
        )}
      </Card.Body>
    </Card>
  );
};

export default MatchResults;
