import React, { useState, useEffect } from 'react';
import { Table, Button, Card, Badge, Spinner } from 'react-bootstrap';
import { jobAPI } from '../services/api';

const MatchResults = ({ jobId, jobTitle }) => {
  const [matches, setMatches] = useState([]);
  const [loading, setLoading] = useState(false);

  const loadMatches = async () => {
    try {
      const response = await jobAPI.getMatches(jobId);
      setMatches(response.data);
    } catch (error) {
      console.error('Error loading matches:', error);
    }
  };

  const runMatching = async () => {
    setLoading(true);
    try {
      await jobAPI.matchResumes(jobId);
      await loadMatches();
    } catch (error) {
      console.error('Error running matches:', error);
    }
    setLoading(false);
  };

  useEffect(() => {
    if (jobId) loadMatches();
  }, [jobId]);

  const getScoreColor = (score) => {
    if (score >= 80) return 'success';
    if (score >= 60) return 'warning';
    return 'danger';
  };

  return (
    <Card>
      <Card.Header className="d-flex justify-content-between align-items-center">
        <h4>Match Results for: {jobTitle}</h4>
        <Button onClick={runMatching} disabled={loading || !jobId}>
          {loading ? <Spinner size="sm" /> : 'Run AI Matching'}
        </Button>
      </Card.Header>
      <Card.Body>
        {matches.length === 0 ? (
          <p>No matches found. Click "Run AI Matching" to analyze resumes.</p>
        ) : (
          <Table striped bordered hover>
            <thead>
              <tr>
                <th>Candidate</th>
                <th>Email</th>
                <th>Match Score</th>
                <th>Matching Skills</th>
                <th>Uploaded</th>
              </tr>
            </thead>
            <tbody>
              {matches.map((match) => (
                <tr key={match._id}>
                  <td>{match.resumeId.candidateName}</td>
                  <td>{match.resumeId.email}</td>
                  <td>
                    <Badge bg={getScoreColor(match.score)}>
                      {match.score}%
                    </Badge>
                  </td>
                  <td>
                    {match.matchingSkills.map((skill, idx) => (
                      <Badge key={idx} bg="info" className="me-1">
                        {skill}
                      </Badge>
                    ))}
                  </td>
                  <td>{new Date(match.resumeId.uploadedAt).toLocaleDateString()}</td>
                </tr>
              ))}
            </tbody>
          </Table>
        )}
      </Card.Body>
    </Card>
  );
};

export default MatchResults;
